import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`;

const Wrapper = styled.div`
    height: 100%;
`;

const Slide = styled.div`
    display: flex;
    align-items: center;
`;

const ImgContainer = styled.div`
    flex: 1;
`;

const Image = styled.div`

`;

const InfoContainer = styled.div`
    flex: 1;
`;

const Slider = () => {
  return (
    <Container>
        <Arrow direction="left">
            <ArrowLeftOutlined/>
        </Arrow>
        <Wrapper>
            <ImgContainer>
                <Image src='https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.blackenterprise.com%2Fthe-will-smith-slap-meme-is-now-a-minted-cryptocurrency-coin%2F&psig=AOvVaw0Y5gXfQuv8nfCZZp47uqxX&ust=1649225515382000&source=images&cd=vfe&ved=0CAoQjRxqFwoTCJj08Pqh_PYCFQAAAAAdAAAAABAD'/>
            </ImgContainer>
            <InfoContainer></InfoContainer>
        </Wrapper>
        <Arrow direction="right">
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
