            </Slide>
            <Slide bg="fcf1ed">
                <ImgContainer>
                    <Image src=""/>
                </ImgContainer>
                <InfoContainer>
                    <Title>Will Slap</Title>
                    <Desc>Will Smith just slapped the shit out of Chris Rock</Desc>
                    <Button>Blame</Button>
                </InfoContainer>
            </Slide>
            <Slide bg="fbf0f4">
                <ImgContainer>
                    <Image src=""/>
                </ImgContainer>
                <InfoContainer>
                    <Title>Will Slap</Title>
                    <Desc>Will Smith just slapped the shit out of Chris Rock</Desc>
                    <Button>Blame</Button>
                </InfoContainer>
            </Slide>
        </Wrapper>
        <Arrow direction="right" onClick={() => handleClick("right")}>
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
